<script>
document.addEventListener('DOMContentLoaded', function() {
  const scrollToElement = localStorage.getItem('scrollTarget');

  if (scrollToElement) {
	var element = document.getElementById(scrollToElement);

	if (element) {
	  element.scrollIntoView({ behavior: "smooth", block: "start", inline: "nearest" });

	  document.getElementById(scrollToElement).click();
	  localStorage.removeItem('scrollTarget');
	}
  }
});
</script>

<script>
	document.addEventListener('DOMContentLoaded', function() {
		// Set initial opacity and translateY to zero
		gsap.set('#brands, #brandsParagraph', {
			opacity: 0,
			y: '2.5rem'
		});

		// Create an Intersection Observer
		const observer = new IntersectionObserver(entries => {
			entries.forEach(entry => {
				if (entry.isIntersecting) {
					// Custom delays for each element
					let delay = 0;

					if (entry.target.id === 'brands') {
						delay = .25;
					} else if (entry.target.id === 'brandsParagraph') {
						delay = .5;
					}
					const targetIds = ['brands', 'brandsParagraph'];

					gsap.fromTo(
						`#${entry.target.id}`, {
							opacity: 0,
							y: '2.5rem'
						}, {
							opacity: 1,
							y: '0',
							duration: .5,
							ease: 'power2.in',
							delay: delay
						}
					);

					// Unobserve the element to avoid triggering the animation again
					observer.unobserve(entry.target);
				}
			});
		}, {
			threshold: 0.5
		});

		observer.observe(document.getElementById('brands'));
		observer.observe(document.getElementById('brandsParagraph'));
	});
</script>

