	<script>
		function showTab(tabId, button) {

			if (event) {
				event.preventDefault();
				// Rest of your code
			}
			// Hide all tab content
			const tabContentItems = document.querySelectorAll(".tab-content");
			tabContentItems.forEach(item => {
				item.style.display = "none";
			});

			// Remove underlined border from all tab buttons
			const tabButtons = document.querySelectorAll(".inline-flex");
			tabButtons.forEach(tabButton => {
				tabButton.style.borderBottom = "2px solid transparent";
				tabButton.style.color = "gray";
			});

			// Show the selected tab content
			const tabToShow = document.getElementById(tabId);
			tabToShow.style.display = "block";

			// Add underlined border to the selected tab button
			button.style.borderBottom = "4px solid #000000";
			button.style.color = "#333"; /* Customize the active tab text color */
		}

		// Initially select the first tab
		showTab('tab1', document.querySelector('.inline-flex'));
	</script>
