<script src="https://unpkg.com/gsap@3.12.2/dist/gsap.min.js"></script>

<script>
	document.addEventListener('DOMContentLoaded', function() {
		// Set initial opacity and translateY to zero
		gsap.set('#impactHeading, #impactParagraph, #whatImpact, #whatParagraph, #impactEnd, #impactEndParagraph, #impactEndHeading', {
			opacity: 0,
			y: '20px'
		});

		// Create an Intersection Observer
		const observer = new IntersectionObserver(entries => {
			entries.forEach(entry => {
				if (entry.isIntersecting) {
					// Custom delays for each element
					let delay = 0;

					if (entry.target.id === 'impactHeading') {
						delay = 0;
					} else if (entry.target.id === 'impactParagraph') {
						delay = .25;
					} else if (entry.target.id === 'whatImpact') {
						delay = 0;
					} else if (entry.target.id === 'whatParagraph') {
						delay = 0.2;
					} else if (entry.target.id === 'impactEnd') {
						delay = 0;
					} else if (entry.target.id === 'impactEndParagraph') {
						delay = 0;
					} else if (entry.target.id === 'impactEndHeading') {
						delay = 0;
					}
					const targetIds = ['impactHeading', 'impactParagraph', 'whatImpact', 'whatParagraph', 'impactEnd', 'impactEndParagraph', 'impactEndHeading'];

					gsap.fromTo(
						`#${entry.target.id}`, {
							opacity: 0,
							y: '20px'
						}, {
							opacity: 1,
							y: '0',
							duration: .7,
							ease: 'power2.in',
							delay: delay
						}
					);

					// Unobserve the element to avoid triggering the animation again
					observer.unobserve(entry.target);
				}
			});
		}, {
			threshold: 0.5
		});

		observer.observe(document.getElementById('impactHeading'));
		observer.observe(document.getElementById('impactParagraph'));
		observer.observe(document.getElementById('whatImpact'));
		observer.observe(document.getElementById('whatParagraph'));
		observer.observe(document.getElementById('impactEnd'));
		observer.observe(document.getElementById('impactEndParagraph'));
		observer.observe(document.getElementById('impactEndHeading'));
	});
</script>


