					x: 0
				});

				const bounceTimeline = gsap.timeline({
					paused: true
				});
				bounceTimeline.to(svgContainer, {
						x: 10,
						duration: 0.3,
						ease: "power1.inOut"
					})
					.to(svgContainer, {
						x: 0,
						duration: 0.3,
						ease: "power1.inOut",
						yoyo: true,
						repeat: 1
					});

				learnMoreLink.addEventListener("mouseenter", function() {
					bounceTimeline.play();
				});

				learnMoreLink.addEventListener("mouseleave", function() {
					bounceTimeline.reverse();
				});
